module.exports = function (app) {
  return {
    id: 'testplugin',
    name: 'Test Plugin Name',
    schema: function () {
      return {
        type: 'object',
        properties: {}
      }
    },
    start: function () {
      app.debug('Demoing debug logging')
      app.error('Demoing error diagnostics')
      this.started = true

      app.handleMessage(this.id, {
        updates: [
          {
            values: [
              {
                path: 'some.path',
                value: 'someValue'
              }
            ]
          }
        ]
      })
    },
    stop: function () {
      this.started = false
    },
    app: app
  }
}
